angular.module("calendarDemoApp",["ui.rCalendar"]),angular.module("calendarDemoApp").controller("CalendarDemoCtrl",["$scope",function($scope){function createRandomEvents(){for(var events=[],i=0;i<20;i+=1){var startTime,endTime,date=new Date,eventType=Math.floor(2*Math.random()),startDay=Math.floor(90*Math.random())-45,endDay=Math.floor(2*Math.random())+startDay;if(0===eventType)startTime=new Date(Date.UTC(date.getUTCFullYear(),date.getUTCMonth(),date.getUTCDate()+startDay)),endDay===startDay&&(endDay+=1),endTime=new Date(Date.UTC(date.getUTCFullYear(),date.getUTCMonth(),date.getUTCDate()+endDay)),events.push({title:"All Day - "+i,startTime:startTime,endTime:endTime,allDay:!0});else{var startMinute=Math.floor(24*Math.random()*60),endMinute=Math.floor(180*Math.random())+startMinute;startTime=new Date(date.getFullYear(),date.getMonth(),date.getDate()+startDay,0,date.getMinutes()+startMinute),endTime=new Date(date.getFullYear(),date.getMonth(),date.getDate()+endDay,0,date.getMinutes()+endMinute),events.push({title:"Event - "+i,startTime:startTime,endTime:endTime,allDay:!1})}}return events}$scope.changeMode=function(mode){$scope.mode=mode},$scope.today=function(){$scope.currentDate=new Date},$scope.isToday=function(){var today=new Date,currentCalendarDate=new Date($scope.currentDate);return today.setHours(0,0,0,0),currentCalendarDate.setHours(0,0,0,0),today.getTime()===currentCalendarDate.getTime()},$scope.loadEvents=function(){$scope.eventSource=createRandomEvents()},$scope.onEventSelected=function(event){$scope.event=event}}]);